function Item({ selectedItem })
{
    return (
        <div className="card mx-auto p-5">
            <p><img id="img" src={selectedItem.Image} className="card-img-top" alt={selectedItem.ItemNum} /></p>
            
            <h1 className="badge text-bg-warning fs-2 mb-4">{selectedItem.ItemNum}</h1>
            <p className="badge text-bg-info fs-5 m-4"><strong>Class: </strong>{selectedItem.Class}</p>
            <p class="border border-white mb-5"><strong className="badge text-bg-info fs-6 m-2">Containment:</strong>{selectedItem.Containment}</p>
            <p class="border border-white"><strong className="badge text-bg-info fs-6 m-2">Description:</strong>{selectedItem.Description}</p>
        </div>
    );
}

export default Item;
