import './App.css';
import Menu from './Menu.js';
import Item from './scpItem.js';
import scpData from './scpData.json'
import { useState, useEffect } from 'react';

// Fetch data from supplied URL
/*function fetchData()
{
  return fetch("https://xajct.github.io/content_hosting/scp.json")
    .then(response => response.json)
}*/

function App()
{

  const selectedItem = {value:null};

  useEffect(
    () =>
    {
      fetchData().then(data => setItems(data))
    }, []
  );

  return (
    <div className='app-container'>
      <Menu Items={scpData} selectedItem={selectedItem} />
      <h1>SCP Foundation</h1>
      {selectedItem.value && <Item selectedItem={selectedItem} />}
    </div>
  );
}

export default App;
